package JavaQuations;

public class AlphabetOrNot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		char c='a';
		if ((c>='a' && c<='z')||(c>='A'&& c<='Z')) {
			System.out.println(c+" :is an Alphabet");
		}else {
			System.out.println(c+" : is an not Alphabet");
		}

	}

}
