package JavaQuations;

public class SumOfNaturalNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		 int num=10;
		 int sum=0;
		 
		 for (int i = 1; i <=num; i++) {
			sum=sum+i;
		}	
		 System.out.println("sum of: " +sum);
		 System.out.println("&&&&&&&&&&&&&&&&&&&&&");
		 
		 
		 int k=1;
		 int sum1=0;
		 
		 while (k<=num) {
			sum1=sum1+k;
			k++;
		}
		 System.out.println("sum1 of: " +sum1);	}

}
